window.onload=function(){if(typeof(speechSynthesis)=="undefined"){var a=document.querySelectorAll(".speaker");for(i=0;i<a.length;i++){a[i].addEventListener("click",function(d){alert("お使いのブラウザは音声再生に対応していないようです。GoogleChromeでの利用をオススメします。")})}return}var c=false;if("onvoiceschanged" in speechSynthesis){speechSynthesis.onvoiceschanged=function(){b()}}else{b()}function b(){if(c){return}c=true;var e=speechSynthesis.getVoices();var d=document.querySelectorAll(".speaker");for(i=0;i<d.length;i++){d[i].addEventListener("click",function(h){var g=new SpeechSynthesisUtterance();g.text=h.target.getAttribute("data-text");g.lang=h.target.getAttribute("data-lang");for(var f=0;f<e.length;f++){if(e[f].lang==g.lang){g.voice=e[f]}}if(!g.voice){alert("ごめんなさい、お使いのブラウザが対応していない言語のようです...");return}speechSynthesis.speak(g)})}}};