window.onload=function(){if(!"SpeechSynthesisUtterance" in window){var a=document.querySelectorAll(".speaker");for(i=0;i<a.length;i++){a[i].addEventListener("click",function(c){alert("お使いのブラウザは音声再生に対応していないようです。GoogleChromeでの利用をオススメします。")},{once:true})}return}var b=false;speechSynthesis.onvoiceschanged=function(){if(b){return}b=true;var d=speechSynthesis.getVoices();var c=document.querySelectorAll(".speaker");for(i=0;i<c.length;i++){c[i].addEventListener("click",function(h){var g=new SpeechSynthesisUtterance();g.text=h.target.getAttribute("data-text");g.lang=h.target.getAttribute("data-lang");for(var f=0;f<d.length;f++){if(d[f].lang==g.lang){g.voice=d[f]}}speechSynthesis.speak(g)},{once:true})}}};
